    private void btnSaqueActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSaqueActionPerformed
        // TODO add your handling code here:
        String strSenha = senhaUsuario.getText();
        TelaError warning = new TelaError();
        int senha = Integer.parseInt(strSenha);
        
        if(senha == 1234) {
            String strSaldo = saldoUsuario.getText();
            String strValor = saqueValor.getText();
        
            int valor = Integer.parseInt(strValor);
            int saldo = Integer.parseInt(strSaldo);
        
        
            int novoSaldo = saldo - valor;
            String saldoPrint = Integer.toString(novoSaldo);
            saldoUsuario.setText(saldoPrint);
        } else {
            warning.setVisible(true);
        }

        
    }//GEN-LAST:event_btnSaqueActionPerformed

